import { useNavigate  } from "react-router-dom";
import { MdEmail, MdLock } from 'react-icons/md'
import { Button } from '../../components/Button';
import { Header } from '../../components/Header';
import { Input } from '../../components/Input';
// import { api } from '../../services/api';

import { useForm } from "react-hook-form";

import { Container, Title, Column, TitleLogin, SubtitleLogin, Row, Wrapper } from './styles';

 const Register = () => {

    const navigate = useNavigate()
    

  return (<>
  <Header />
        <Container>
            <Column>
                <Title>A plataforma para você aprender com experts, dominar as principais tecnologias
                 e entrar mais rápido nas empresas mais desejadas.</Title>
            </Column>
            <Column>
                <Wrapper>
                <TitleLogin>Faça seu Cadastro</TitleLogin>
                <SubtitleLogin>Faça seu cadastro de novo usuário e make the change._</SubtitleLogin>
                <form >
                    <Input placeholder="E-mail" leftIcon={<MdEmail />} name="email"   />
                    {/* {errors.email && <span>E-mail é obrigatório</span>} */}
                    <Input type="password" placeholder="Senha" leftIcon={<MdLock />}  name="senha"  />
                    {/* {errors.senha && <span>Senha é obrigatório</span>} */}
                    <Button title="Entrar" variant="secondary" type="submit"/>
                </form>
                <Row>
                    
                </Row>
                </Wrapper>
            </Column>
        </Container>
 
  </>)
}

export { Register }
